import React, { useEffect, useState } from "react";
import axios from "axios";

const Clientes = () => {
  const [clientes, setClientes] = useState([]);

  useEffect(() => {
    // Buscar os clientes do backend
    axios.get("http://localhost:8000/clientes")
      .then((response) => {
        console.log("Clientes recebidos:", response.data); // Verifique os dados no console
        setClientes(response.data);
      })
      .catch((error) => console.error("Erro ao buscar clientes:", error));
  }, []);

  return (
    <div>
      <h1>Lista de Clientes</h1>
      {clientes.length > 0 ? (
        <ul>
          {clientes.map((cliente) => (
            <li key={cliente.id}>{cliente.nome} - {cliente.email}</li>
          ))}
        </ul>
      ) : (
        <p>Nenhum cliente encontrado.</p>  // Exibe uma mensagem se não houver clientes
      )}
    </div>
  );
};

export default Clientes;
